  #{postDate,jdbcType=TIMESTAMP}, #{lastUpdateDate,jdbcType=TIMESTAMP}, #{archiveTitle,jdbcType=VARCHAR}, 
  #{isReward,jdbcType=INTEGER}, #{markdown,jdbcType=LONGVARCHAR}, #{html,jdbcType=LONGVARCHAR}
  )
